import React, { useState,useEffect} from 'react'

const AddBlog = (props) => {
  
  console.log(props);

  const{ Author }=props.name;


  const[title,setTitle] = useState("");  
  const[date,setDate] = useState("");
  const [article,setArticle] = useState("");


  const combine = {Author,title,date,article}

  
  const LOCAL_STORAGE_KEY=  " combine"


  const NewBlog = (e) =>{
    
    useEffect(()=> {

      localStorage.setItem(LOCAL_STORAGE_KEY,JSON.stringify(combine))
    },[combine]);
    
   
    }

  return (
    <div className='ui main'>
        <form className='ui form' onSubmit={NewBlog}>
          <div className='ui field'>

            <label> Author </label>
            <label>{Author}</label>

            <label> Title </label>
            <input 
            type="text" 
            name="title" 
            placeholder='Title of the article'
            value={title}
            onChange={(e)=>setTitle(e.target.value)}/>

            <label> Date </label>
            <input 
            type="date" 
            name="date" 
            placeholder='Date'
            value={date}
            onChange={(e)=>setDate(e.target.value)}/>


            <label className='ui vertical segment'> Article </label>
            <textarea 
            type="text" name="article" placeholder='Enter article here'
             minLength={100} height = {5} width= "10px" rows="12"
             value={article}
             onChange={(e)=>setArticle(e.target.value)}
             />            


            <br/>

            <button  className='ui button'> Submit my article </button>


          </div>

        </form>

    </div>
  )
}

export default AddBlog;
