import React, {useEffect, useState} from 'react'

import { NavLink } from 'react-router-dom';

const Blog = () => {

  const[combine,setCombine]=useState("");
  const LOCAL_STORAGE_KEY="combine";


  useEffect(()=> {

    const retcombine =JSON.parse(localStorage.getItem(LOCAL_STORAGE_KEY));
    if(retcombine)setCombine(retcombine);    
    },[]);
    
    function remove(){

    localStorage.removeItem('Author');
    localStorage.removeItem('Date');
    localStorage.removeItem('Title');
    localStorage.removeItem('Article');
  }
  return (
    <div class="ui segment">


      <a class="ui red ribbon label">Article</a>
      
      <h2  class="ui  header">{retcombine.Author}</h2>

      <h4 class="ui header">
      Author: <p>{retcombine.Author}</p>
      </h4>


      <div class="ui label">
      Published Date: <p>{retcombine.date}</p>
      </div>

      <h4 class="ui header"> Article </h4>

      <div class="ui segment">
      <p>{retcombine.Author}</p>
      </div>

      <button class="ui secondary basic button" ><NavLink to="/edit"> Edit </NavLink></button>
      <button class="ui negative basic button" onClick={remove}>Delete</button>
    </div>
  )
}

export default Blog
